import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { flightSearchSchema, insertFlightSchema, insertBookingSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Aircraft routes
  app.get("/api/aircraft", async (req, res) => {
    try {
      const aircraft = await storage.getAircraft();
      res.json(aircraft);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch aircraft" });
    }
  });

  app.get("/api/aircraft/:id", async (req, res) => {
    try {
      const aircraft = await storage.getAircraftById(req.params.id);
      if (!aircraft) {
        return res.status(404).json({ message: "Aircraft not found" });
      }
      res.json(aircraft);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch aircraft" });
    }
  });

  app.get("/api/aircraft/category/:category", async (req, res) => {
    try {
      const aircraft = await storage.getAircraftByCategory(req.params.category);
      res.json(aircraft);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch aircraft by category" });
    }
  });

  // Flight search route
  app.post("/api/flights/search", async (req, res) => {
    try {
      const searchData = flightSearchSchema.parse(req.body);
      const aircraft = await storage.searchFlights(searchData);
      res.json(aircraft);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid search parameters", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to search flights" });
    }
  });

  // Flight routes
  app.post("/api/flights", async (req, res) => {
    try {
      const flightData = insertFlightSchema.parse(req.body);
      const flight = await storage.createFlight(flightData);
      res.status(201).json(flight);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid flight data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create flight" });
    }
  });

  app.get("/api/flights/user/:userId", async (req, res) => {
    try {
      const flights = await storage.getFlightsByUser(req.params.userId);
      res.json(flights);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch user flights" });
    }
  });

  app.get("/api/flights/:id", async (req, res) => {
    try {
      const flight = await storage.getFlightById(req.params.id);
      if (!flight) {
        return res.status(404).json({ message: "Flight not found" });
      }
      res.json(flight);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch flight" });
    }
  });

  app.patch("/api/flights/:id/status", async (req, res) => {
    try {
      const { status } = req.body;
      const flight = await storage.updateFlightStatus(req.params.id, status);
      if (!flight) {
        return res.status(404).json({ message: "Flight not found" });
      }
      res.json(flight);
    } catch (error) {
      res.status(500).json({ message: "Failed to update flight status" });
    }
  });

  // Booking routes
  app.post("/api/bookings", async (req, res) => {
    try {
      const bookingData = insertBookingSchema.parse(req.body);
      const booking = await storage.createBooking(bookingData);
      res.status(201).json(booking);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid booking data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create booking" });
    }
  });

  app.get("/api/bookings/user/:userId", async (req, res) => {
    try {
      const bookings = await storage.getBookingsByUser(req.params.userId);
      res.json(bookings);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch user bookings" });
    }
  });

  app.patch("/api/bookings/:id/status", async (req, res) => {
    try {
      const { status } = req.body;
      const booking = await storage.updateBookingStatus(req.params.id, status);
      if (!booking) {
        return res.status(404).json({ message: "Booking not found" });
      }
      res.json(booking);
    } catch (error) {
      res.status(500).json({ message: "Failed to update booking status" });
    }
  });

  // Destinations routes
  app.get("/api/destinations", async (req, res) => {
    try {
      const destinations = await storage.getDestinations();
      res.json(destinations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch destinations" });
    }
  });

  app.get("/api/destinations/popular", async (req, res) => {
    try {
      const destinations = await storage.getPopularDestinations();
      res.json(destinations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch popular destinations" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Aircraft, type InsertAircraft, type Flight, type InsertFlight, type Booking, type InsertBooking, type Destination, type InsertDestination, type FlightSearch } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Aircraft
  getAircraft(): Promise<Aircraft[]>;
  getAircraftById(id: string): Promise<Aircraft | undefined>;
  getAircraftByCategory(category: string): Promise<Aircraft[]>;
  createAircraft(aircraft: InsertAircraft): Promise<Aircraft>;

  // Flights
  searchFlights(search: FlightSearch): Promise<Aircraft[]>;
  createFlight(flight: InsertFlight): Promise<Flight>;
  getFlightsByUser(userId: string): Promise<Flight[]>;
  getFlightById(id: string): Promise<Flight | undefined>;
  updateFlightStatus(id: string, status: string): Promise<Flight | undefined>;

  // Bookings
  createBooking(booking: InsertBooking): Promise<Booking>;
  getBookingsByUser(userId: string): Promise<Booking[]>;
  getBookingById(id: string): Promise<Booking | undefined>;
  updateBookingStatus(id: string, status: string): Promise<Booking | undefined>;

  // Destinations
  getDestinations(): Promise<Destination[]>;
  getPopularDestinations(): Promise<Destination[]>;
  createDestination(destination: InsertDestination): Promise<Destination>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private aircraft: Map<string, Aircraft>;
  private flights: Map<string, Flight>;
  private bookings: Map<string, Booking>;
  private destinations: Map<string, Destination>;

  constructor() {
    this.users = new Map();
    this.aircraft = new Map();
    this.flights = new Map();
    this.bookings = new Map();
    this.destinations = new Map();

    // Initialize with sample data
    this.initializeSampleData();
  }

  private initializeSampleData() {
    // Sample Aircraft
    const sampleAircraft: Aircraft[] = [
      {
        id: randomUUID(),
        model: "Citation CJ3+",
        category: "light",
        manufacturer: "Cessna",
        passengers: 7,
        range: 2040,
        speed: 478,
        hourlyRate: "3500.00",
        description: "Perfect for short to medium-range flights with luxury amenities.",
        imageUrl: "https://images.unsplash.com/photo-1556388158-158ea5ccacbd?ixlib=rb-4.0.3",
        available: true,
      },
      {
        id: randomUUID(),
        model: "Hawker 800XP",
        category: "midsize",
        manufacturer: "Hawker Beechcraft",
        passengers: 8,
        range: 2540,
        speed: 514,
        hourlyRate: "4800.00",
        description: "Ideal for coast-to-coast flights with spacious cabin comfort.",
        imageUrl: "https://images.unsplash.com/photo-1540962351504-03099e0a754b?ixlib=rb-4.0.3",
        available: true,
      },
      {
        id: randomUUID(),
        model: "Gulfstream G650",
        category: "heavy",
        manufacturer: "Gulfstream",
        passengers: 14,
        range: 7000,
        speed: 652,
        hourlyRate: "8500.00",
        description: "Ultra-long-range luxury for international travel with unmatched comfort.",
        imageUrl: "https://images.unsplash.com/photo-1583473848882-f9a5bc7fd2ee?ixlib=rb-4.0.3",
        available: true,
      },
    ];

    sampleAircraft.forEach(aircraft => this.aircraft.set(aircraft.id, aircraft));

    // Sample Destinations
    const sampleDestinations: Destination[] = [
      {
        id: randomUUID(),
        name: "Miami International Airport",
        city: "Miami",
        state: "FL",
        country: "USA",
        airportCode: "MIA",
        description: "Luxury beaches and vibrant nightlife",
        imageUrl: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3",
        popular: true,
      },
      {
        id: randomUUID(),
        name: "Aspen/Pitkin County Airport",
        city: "Aspen",
        state: "CO",
        country: "USA",
        airportCode: "ASE",
        description: "World-class skiing and mountain luxury",
        imageUrl: "https://images.unsplash.com/photo-1551524164-687a55dd1126?ixlib=rb-4.0.3",
        popular: true,
      },
      {
        id: randomUUID(),
        name: "Napa County Airport",
        city: "Napa",
        state: "CA",
        country: "USA",
        airportCode: "APC",
        description: "Premium wine country experiences",
        imageUrl: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3",
        popular: true,
      },
    ];

    sampleDestinations.forEach(destination => this.destinations.set(destination.id, destination));
  }

  // Users
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id,
      createdAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  // Aircraft
  async getAircraft(): Promise<Aircraft[]> {
    return Array.from(this.aircraft.values()).filter(aircraft => aircraft.available);
  }

  async getAircraftById(id: string): Promise<Aircraft | undefined> {
    return this.aircraft.get(id);
  }

  async getAircraftByCategory(category: string): Promise<Aircraft[]> {
    return Array.from(this.aircraft.values()).filter(
      aircraft => aircraft.category === category && aircraft.available
    );
  }

  async createAircraft(insertAircraft: InsertAircraft): Promise<Aircraft> {
    const id = randomUUID();
    const aircraft: Aircraft = { ...insertAircraft, id };
    this.aircraft.set(id, aircraft);
    return aircraft;
  }

  // Flights
  async searchFlights(search: FlightSearch): Promise<Aircraft[]> {
    let results = Array.from(this.aircraft.values()).filter(aircraft => aircraft.available);

    if (search.category) {
      results = results.filter(aircraft => aircraft.category === search.category);
    }

    results = results.filter(aircraft => aircraft.passengers >= search.passengers);

    return results;
  }

  async createFlight(insertFlight: InsertFlight): Promise<Flight> {
    const id = randomUUID();
    const flight: Flight = { 
      ...insertFlight, 
      id,
      createdAt: new Date(),
    };
    this.flights.set(id, flight);
    return flight;
  }

  async getFlightsByUser(userId: string): Promise<Flight[]> {
    return Array.from(this.flights.values()).filter(flight => flight.userId === userId);
  }

  async getFlightById(id: string): Promise<Flight | undefined> {
    return this.flights.get(id);
  }

  async updateFlightStatus(id: string, status: string): Promise<Flight | undefined> {
    const flight = this.flights.get(id);
    if (flight) {
      flight.status = status;
      this.flights.set(id, flight);
    }
    return flight;
  }

  // Bookings
  async createBooking(insertBooking: InsertBooking): Promise<Booking> {
    const id = randomUUID();
    const booking: Booking = { 
      ...insertBooking, 
      id,
      createdAt: new Date(),
    };
    this.bookings.set(id, booking);
    return booking;
  }

  async getBookingsByUser(userId: string): Promise<Booking[]> {
    return Array.from(this.bookings.values()).filter(booking => booking.userId === userId);
  }

  async getBookingById(id: string): Promise<Booking | undefined> {
    return this.bookings.get(id);
  }

  async updateBookingStatus(id: string, status: string): Promise<Booking | undefined> {
    const booking = this.bookings.get(id);
    if (booking) {
      booking.status = status;
      this.bookings.set(id, booking);
    }
    return booking;
  }

  // Destinations
  async getDestinations(): Promise<Destination[]> {
    return Array.from(this.destinations.values());
  }

  async getPopularDestinations(): Promise<Destination[]> {
    return Array.from(this.destinations.values()).filter(destination => destination.popular);
  }

  async createDestination(insertDestination: InsertDestination): Promise<Destination> {
    const id = randomUUID();
    const destination: Destination = { ...insertDestination, id };
    this.destinations.set(id, destination);
    return destination;
  }
}

export const storage = new MemStorage();

